func TestCodeforcesParser(t *testing.T) {
	Convey("It should implements Parser interface", t, func() {
		parser := new(CodeforcesParser)
		So(parser, ShouldImplement, (*Parser)(nil))
	})
}

func TestCodeforcesParser_ParseTaskFromHTML(t *testing.T) {
	Convey("It should return correct task", t, func() {
		parser := new(CodeforcesParser)
		task, err := parser.ParseTaskFromHTML(task962A)
		So(err, ShouldBeNil)
		So(task, ShouldNotBeNil)
		log.Debug().Msgf("%v", task)
	})
}

